import React from "react";
import { ComponentSizeENUM, ThemeENUM, TypographyENUM } from "../../../../../../Types/general";
import PrimaryButton from "../../../../../Common/PrimaryButton";
import TryOn from "../../../../../Common/Try On Button";
import { ActionButtonWrapper, AddToCartButtonWrapper } from "./actionButtons.styles";
import { AddToCartButtonItemType, CartButtonsType } from "./actionButtons.types";

const ActionButtons = ({ isCygnusEnabled, font=TypographyENUM.defaultBook, addToCartButtons, onButtonClick, onTryOnClick, showTryOn=true, isRTL=false, locale, customProductQty }: CartButtonsType) => {
	return (
		<ActionButtonWrapper>
			<AddToCartButtonWrapper isRTL={isRTL}>
				{addToCartButtons?.map((button: AddToCartButtonItemType, index: number) => {
					return (
						<PrimaryButton
							primaryText={locale[button.primaryText]}
							secondaryText={locale[button.secondaryText as string]}
							key={index}
							font={font}
							componentSize={ComponentSizeENUM.medium}
							onBtnClick={()=> onButtonClick(button.onlyLens, button.withPower, customProductQty)}
							id="btn-primary"
							width={addToCartButtons.length === 1 ? "100%" : "48%"}
							height="55px"
							theme={index === 0 ? ThemeENUM.primary : ThemeENUM.secondary}
						/>);
				})}
			</AddToCartButtonWrapper>
			{showTryOn && isCygnusEnabled && <TryOn id="btn-try-on" width="100%" height="55px" imagePosition="var(--spacing-md)" tryOnImageURL="https://static.lenskart.com/media/desktop/img/pdp/try_on_model.png" onBtnClick={onTryOnClick} isRTL={isRTL} text="Try On"></TryOn> }
		</ActionButtonWrapper>
	);
};

export default ActionButtons;
