import React from "react";
import { BasicDetailsWrapper, Brand, Size, SizeWrapper, InfoSection, Title } from "./basicDetails.styles";
import { BasicDetailsType } from "./basicDetails.types";
import { Info } from "../../../../../../Icons";

const BasicDetails = ({ brandName, productName, size, showInfo = true, onInfoClick, font }: BasicDetailsType) => {
	return (
		<BasicDetailsWrapper styleFont={font}>
			<Brand>{brandName}</Brand>
			<Title>{productName}</Title>
			<SizeWrapper>
				<Size>{size}</Size>{showInfo && <InfoSection onClick={onInfoClick}><Info /></InfoSection>}
			</SizeWrapper>
		</BasicDetailsWrapper>
	);
};

export default BasicDetails;
