import React from "react";
import { OfferDisplayType } from "./offerDisplay.types";
import { ProductOfferDetailsWithIcon, ProductOfferDetails, OfferDetailsText1, OfferDetailsText2 } from "./offerDisplay.styles";


const OfferDisplay = ({ headline1, headline2, icon }: OfferDisplayType) => {
	return (
		<ProductOfferDetailsWithIcon>
			<>
				<ProductOfferDetails >
					{headline1 && headline1.length>0 && <OfferDetailsText1>{headline1}</OfferDetailsText1>}
					{headline2 && headline2.length>0 && <OfferDetailsText2>{headline2}</OfferDetailsText2>}
				</ProductOfferDetails>
				{/* {icon && icon.length>0 && icon.toLowerCase() !== "applied" && (
					<img
						alt="right-icon"
						src="https://static.lenskart.com/media/desktop/img/DesignStudioIcons/RightArrow.svg"
					/>
				)} */}
				{icon && icon.length>0 && icon.toLowerCase() === "applied" && (
					<img
						alt="stepper"
						src="https://static.lenskart.com/media/desktop/img/DesignStudioIcons/TickIconWithBlueBgRound.svg"
					/>
				)}
			</>
		</ProductOfferDetailsWithIcon>
	);
};

export default OfferDisplay;