import React from "react";
import { QantityInput, QuantityBox, QuantityPanel, QuantityText } from "./qtyInput.styles";


 interface QtyInputPanelType {
    labelText: string;
    setCustomProductQty: (arg1:string)=>void;
    customProductQty: string
}


const QtyInputPanel = ({ labelText, setCustomProductQty, customProductQty }:QtyInputPanelType)=>{
	const onChange = (e:any)=>{
		console.log("typeof",typeof e.target.value);
		
		if(!isNaN(e.target.value)){
			setCustomProductQty(e.target.value);
		}
	};
	const onBlur = (e:any)=>{
		if(isNaN(e.target.value) || e.target.value==="0"){
			setCustomProductQty("1");
		}
	};
	return(
		<QuantityPanel>
			<QuantityBox>
				<QuantityText>{labelText}</QuantityText>
				<QantityInput type="text" value={customProductQty} onChange={onChange} onBlur={onBlur}/>
			</QuantityBox>
		</QuantityPanel>
	);
};

export { QtyInputPanel };