import React from "react";
import { ReviewCardType } from "./ReviewCard.types";
import { ReviewDescription, ReviewerDetails, ReviewTitle, ReviewWrapper } from "./ReviewCard.styles";
import { ComponentSizeENUM, TypographyENUM } from "../../../../../Types/general";
import { Rating } from "../../../../Common/Rating/Rating.component";

const ReviewCard = ({
	reviewId="387621028",
	reviewTitle,
	reviewDetail,
	reviewee,
	reviewDate,
	noOfStars,
	images,
	font= TypographyENUM.defaultBook,
	isRTL = false } : ReviewCardType) => {
	return (
		<ReviewWrapper id={reviewId} styleFont={font}>
			<Rating isReadOnly={true} id="reviewer-rating" rating={Number(noOfStars)} maxRating={5} componentSize={ComponentSizeENUM.medium} onStarsChange={() => null} isRTL={isRTL}></Rating>
			<ReviewTitle isRTL={false}>{reviewTitle}</ReviewTitle>
			<ReviewDescription isRTL={false}>{reviewDetail}</ReviewDescription>
			<ReviewerDetails isRTL={false}>
				{`${reviewee} - ${new Date(reviewDate).toLocaleString("default", { day:"numeric", month: "short", year:"numeric" })}`}
			</ReviewerDetails>
			{images?.length ? <div>{images.map((img) => <img key={img} src={img} alt="img"/>)}</div> : null}
		</ReviewWrapper>
	);
};

export { ReviewCard };
