import React from "react";
import { TypographyENUM } from "../../../../Types/general";
import { ItemKey, ItemValue, ItemValueSpan, ItemWrapper, QuestionMarkIcon, ValueContainer } from "./TechnicalInfoItem.styles";
import { TechnicalInfoItemTypes } from "./TechnicalInfoItem.types";

function TechnicalInfoItem({
	name,
	value,
	additionalInfoUrl,
	isClickable=true,
	showAdditionalInfo=false,
	triggerShowInfo = () => console.log(""),
	font=TypographyENUM.defaultBook,
	index = 10,
	getClick = () => console.log("")

}: TechnicalInfoItemTypes) {

	return (
		<ItemWrapper styleFont={font}>
			<ItemKey>{name}</ItemKey>
			<ValueContainer>
				{name === "PRODUCT_ID" || (showAdditionalInfo && index <= 3) ? <ItemValue href={isClickable && index <= 3 ? additionalInfoUrl?.replace("https://sa.lenskart.com", window.location.origin)?.replace("https://www.lenskart.com", window.location.origin) : undefined} target="_blank">
					{value}
					{showAdditionalInfo && index > 3 ? (
						<QuestionMarkIcon onClick={() => {
							getClick(additionalInfoUrl || "");
							triggerShowInfo();
						}}>?</QuestionMarkIcon>
					) : null}
				</ItemValue>
					: <ItemValueSpan >
						{value}
						{showAdditionalInfo && index > 3 ? (
							<QuestionMarkIcon onClick={() => {
								getClick(additionalInfoUrl || "");
								triggerShowInfo();
							}}>?</QuestionMarkIcon>
						) : null}
					</ItemValueSpan>}
			</ValueContainer>
		</ItemWrapper>
	);
}

export { TechnicalInfoItem };
