import React from "react";
import { TechnicalDataType } from "./TechnicalInfo.types";
import { InfoWrapper, QuestionMarkLink, TechInfoKey, TechInfoLine, TechInfoLink, TechInfoVal } from "./TechnicalInfo.styles";
import { TypographyENUM } from "../../../../Types/general";

const TechnicalInfo = ({
	id="technical-info",
	data,
	font=TypographyENUM.defaultBook,
	removeDomainName,
	subdirectoryPath,
	onShowAllClick = () => console.log("show all clicked"), isRTL=false } : TechnicalDataType) => {

	return (
		<InfoWrapper id={id} styleFont={font} isRTL={isRTL}>
			{data.map((techItem,index) => <TechInfoLine key={index} isRTL={isRTL}>
				<TechInfoKey isRTL={isRTL}>{techItem.name}</TechInfoKey>
				<TechInfoVal isRTL={isRTL}>{techItem.value} {techItem.showAdditionalInfo ? <QuestionMarkLink href={techItem.additionalInfoUrl ? `${subdirectoryPath}${removeDomainName(techItem.additionalInfoUrl, subdirectoryPath)}` : undefined}>?</QuestionMarkLink> : null}</TechInfoVal>
			</TechInfoLine>)}
			<TechInfoLink onClick={onShowAllClick}>Show All Information</TechInfoLink>
		</InfoWrapper>
	);
};

export { TechnicalInfo };
