import React from "react";
import { ButtonWrapper, FlexWrapper, LeftWrapper, RightWrapper, PowerText, Title, Description } from "./styles";
import { PackageRadioTypes } from "./PackageRadio.types";
import { ComponentSizeENUM, TypographyENUM } from "../../../Types/general";

/**
 * Default PackageRadio7 Component
 */
const PackageRadio = ({
	id,
	width = "50",
	componentSize = ComponentSizeENUM.small,
	font = TypographyENUM.defaultBook,
	packageDesc,
	isSelected,
	onClick,
	title,
	imageUrl,
	isRTL = false
}: PackageRadioTypes) => {

	return (
		<ButtonWrapper
			id={id}
			width={width}
			styleSize={componentSize}
			data-testid="radio-image-testid"
			isBorder = {isSelected}
			onClick={() => onClick(id, title)}
		>
			<FlexWrapper data-testid="button-text" isRTL={isRTL}>
				<LeftWrapper>
					{imageUrl && (
						<img alt={id} className="padding-r25" src={imageUrl} />
					)}
					<PowerText styleFont={font} isRTL={isRTL}>
						<Title styleSize={componentSize} >{title}</Title>
						<Description
							dangerouslySetInnerHTML={{ __html: packageDesc }}
						></Description>
					</PowerText>
					{/* {isRTL && imageUrl && (
						<img alt={id} className="padding-r25" src={imageUrl} />
					)} */}
				</LeftWrapper>
				<RightWrapper>
					<img
						alt="check"
						className="cursor-pointer"
						src={` ${
							isSelected
								? "https://static.lenskart.com/media/desktop/img/pdp/check_enabled.png"
								: "https://static.lenskart.com/media/desktop/img/pdp/check_disabled.png"
						}`}
					/>
				</RightWrapper>
			</FlexWrapper>
		</ButtonWrapper>
	);
};
export default PackageRadio;
