import React from "react";
import { Wrapper } from "./styles";
import { PackageRadioGroupTypes, prescriptionTypeDataType } from "./PackageRadioGroup.types";
import { ComponentSizeENUM } from "../../../Types/general";
import PackageRadio from "../PackageRadio";

/**
 * Default PackageRadioGroup Component
 */
const PackageRadioGroup = ({
	id,
	componentSize = ComponentSizeENUM.small,
	prescriptionTypeData,
	selectedId,
	radioWidth = "100",
	onChange,
	isRTL = false
}: PackageRadioGroupTypes) => {

	return (
		<Wrapper id={id}>
			{prescriptionTypeData?.map((item: prescriptionTypeDataType) => {
				return (
					<PackageRadio
						key={item.id}
						id={item.id}
						width = {radioWidth}
						componentSize = {componentSize}
						packageDesc={item.subText}
						isSelected={item.id === selectedId}
						onClick={() => onChange(item.id, item.title)}
						title={item.title}
						imageUrl={item.imageUrl}
						isRTL={isRTL}
					/>
				);
			})}
		</Wrapper>
	);
};
export default PackageRadioGroup;
