import React from "react";
import * as Icons from "../../../../Icons/index";
import { AboutEyePowerContainer, Heading, RowContainer, SubHeading, SubHeadingContainer } from "./AboutEyePower.styles";

interface DataType {
	[name: string]: string
}

export interface AboutEyeTpe {
	infoSections: string[]
	dataLocale: DataType
	isRTL: boolean
}

const AboutEyePower = (props: AboutEyeTpe) => {
	const { dataLocale : { WHAT_ABOUT_MY_EYE_POWER }, infoSections } = props;
	return (
		<AboutEyePowerContainer>
			<Heading>
				<h2>
					{WHAT_ABOUT_MY_EYE_POWER}
				</h2>
			</Heading>
			<SubHeadingContainer>
				{infoSections?.map((info, index) => (
					<RowContainer key={index}>
						<Icons.CheckFilled />
						<SubHeading>
							{info}
						</SubHeading>
					</RowContainer>

				))}
			</SubHeadingContainer>
		</AboutEyePowerContainer>
	);
};

export default AboutEyePower;