import React from "react";
import * as Icons from "../../../../Icons/index";
import { LeftSection, PackageButton, PackageFooterContainer, Price, RightSection, Text, IconWrapper } from "./PackageFooter.styles";
import { PackageFooterTypes } from "./PackageFooter.types";

const PackageFooter = (props: PackageFooterTypes) => {

	const { price, buttonText, onClick, isDisabled, dataLocale, symbol, subtotalText, showButton, isRTL } = props;
	return (
		<PackageFooterContainer>
			<LeftSection>
				<Text>
					{dataLocale?.SUBTOTAL || "Subtotal"} {subtotalText}:
				</Text>
				<Price>
					{symbol} {price}
				</Price>
			</LeftSection>
			{showButton && (<RightSection>
				<PackageButton disabled={isDisabled} onClick={onClick} isRTL={isRTL}>
					<Text disabled={isDisabled} className="btnContinue" isRTL={isRTL}>
						{buttonText}
					</Text>
					<IconWrapper isRTL={isRTL} >
						<Icons.IconRight />
					</IconWrapper>
				</PackageButton>
			</RightSection>)}
		</PackageFooterContainer>
	);
};

export default PackageFooter;
