import React from "react";
import * as Icons from "../../../../Icons/index";
import { ContentWrapper, Description, IconContainer, ImageWrapper, PackageItemWrapper, RightSection, Title } from "./PackageItem.styles";
import { PackageItemType } from "./PackageItem.types";

const PackageItem = (props: PackageItemType) => {
	const { id, onClick, title, imageUrl, packageDesc, isRTL } = props;
	return (
		<PackageItemWrapper
			id={id}
			onClick={() => onClick(id, title)}
		>
			{imageUrl && (<ImageWrapper>
				<img alt={id} src={imageUrl} />
			</ImageWrapper>)}
			<RightSection>
				<ContentWrapper>
					<Title>
						{title}
					</Title>
					<Description dangerouslySetInnerHTML={{ __html: packageDesc }}>
					</Description>
				</ContentWrapper>
				<IconContainer isRTL={isRTL}>
					<Icons.IconRight />
				</IconContainer>
			</RightSection>
		</PackageItemWrapper>

	);
};

export default PackageItem;