import React from "react";
import * as Icons from "../../../../Icons/index";
import PackageItem from "../PackageItem/PackageItem";
import { DescriptiveText, PackageItemGroupWrapper, Text } from "./PackageItemGroup.styles";
import { PackageItemGroupTypes, prescriptionTypeDataType } from "./PackageItemGroup.types";

const PackageItemGroup = (props: PackageItemGroupTypes) => {
	const { id, prescriptionTypeData, selectedId, onChange, isRTL, descriptiveText, helplineNumber, localeData } = props;
	return (
		<PackageItemGroupWrapper isRTL={isRTL} id={id}>
			{prescriptionTypeData?.map((item : prescriptionTypeDataType) => {
				return (
					<PackageItem
						key={item.id}
						id={item.id}
						packageDesc={item.subText}
						isSelected={item.id === selectedId}
						onClick={() => onChange(item.id, item.title)}
						title={item.title}
						imageUrl={item.imageUrl}
						isRTL={isRTL}
					/>
				);
			})}
			{helplineNumber !== "" && (<DescriptiveText>
				<Icons.PhoneNew />
				<Text color="#66668E">{descriptiveText}</Text>
				<Text fontWeight={"var(--fw-bold)"}> { localeData?.CALL || "Call" } {helplineNumber}</Text>
			</DescriptiveText>
			)}
		</PackageItemGroupWrapper>
	);
};
export default PackageItemGroup;
