import React from "react";
import LeftSection from "./LeftSection";
import { PackageScreenType } from "./PackagesScreen.types";
import RightSection from "./RightSection";
import { PackagesContainer } from "./styles";

const PackagesScreen = ({
	id,
	leftSectionId,
	sliderImages,
	rightSectionId,
	sequences,
	heading,
	onClosePackages,
	isVisible,
	children,
	backButton,
	isRTL=false }: PackageScreenType) => {
	return (
		<PackagesContainer id={id} isVisible={isVisible} isRTL={false}>
			<LeftSection sliderImages={sliderImages} id={leftSectionId} isRTL={isRTL}/>
			<RightSection
				id={rightSectionId}
				heading={heading}
				sequences={sequences}
				onClosePackages={onClosePackages}
				backButton={backButton}
			>
				{children}
			</RightSection>
		</PackagesContainer>
	);
};

export default PackagesScreen;
