import { DropdownWrapper, Option, SelectBox } from "./styles";
import { DropdownTypes } from "./Dropdown.types";
import { ComponentSizeENUM, TypographyENUM } from "../../../Types/general";
import React from "react";

const PlaceholderDropdown = ({
	options,
	componentSize = ComponentSizeENUM.small,
	font = TypographyENUM.serif,
	placeholder,
	handleChange,
	value,
	hasError,
	disabled
}: DropdownTypes) => {
	return (
		<DropdownWrapper hasError={hasError} styleSize={componentSize} styleFont={font}>
			<SelectBox onChange={(event) => handleChange(event)} value={value} disabled={disabled}>
				{placeholder && <Option value="" disabled>{placeholder}</Option>}
				{options.map((item, index) => (
					<Option key={index} value={item.value}>
						{item.key}
					</Option>
				))}
			</SelectBox>
		</DropdownWrapper>
	);
};

export default PlaceholderDropdown;
