import React from "react";
import { FlexDiv, Root } from "./PlpSkeleton.styles";
import Skeleton from "react-loading-skeleton";

const PlpSkeleton = ({ width, cards, height }: {width: number, cards: number, height: number}) => {
	return (
		<Root cards={cards}>
			<Skeleton width={width * 7/10} />
			<Skeleton height={height} />
			<FlexDiv><Skeleton width={((width * 3)/10)} /><Skeleton width={width * 4/10} />
			</FlexDiv>
			<FlexDiv> <Skeleton width={width * 4/10} /><Skeleton width={width * 4/10} />
			</FlexDiv>
			<FlexDiv><Skeleton width={width * 2/10} />
			</FlexDiv></Root>
	);
};

export default PlpSkeleton;