export const Div1Css = css`
    margin-bottom: 20px;
    display: flex;

    input {
      display: none;
    }
    label {
      margin-left: 23px;

      &::before {
        left: 14px;
        top: 24px;
        border: 1px solid #a2a2b8;
        width: 16px;
        height: 16px;
        // border-radius: 50%;
        border-radius: 4px;
        content: '';
        position: absolute;
        transition: all 0.3s ease;
        transition-property: transform, border-color, -webkit-transform;
      }

      &::after {
        left: 14px;
        top: 24px;
        width: 16px;
        height: 16px;
        // border-radius: 50%;
        border-radius: 4px;
        background: #000042;
        position: absolute;
      }
    }
    input[type='checkbox']:checked + label::before {
        width: 4px;
        height: 9px;
        border: solid #fff;
        border-width: 0 1px 1px 0;
        -webkit-transform: rotate(45deg);
        transform: rotate(45deg);
        margin: 5px 7px;
        z-index: 1;
        border-radius: 0px;
        position: absolute;
        left: 13px;
        top: 22px;
      }
      input[type='checkbox']:checked + label::after {
        background: #000042;
        position: absolute;
        transform: scale(1);
        content: '';
        left: 0;
        left: 14px;
        top: 24px;
      }
  }
  `;

