
import React from "react";
import { TypographyENUM } from "../../../Types/general";
import { PayLater } from "../NewPayment/components/subcomponents/PayLater";
import ContactLensConsentCheckbox from "../NewPayment/ContactLensConsentCheckbox";
import { PrimeCardType } from "./PrimeCard.types";
import { PrimerCardWrapper, PayNowBtn, PayLaterButtonWrapper } from "./styles";


const PrimerCard = ({ deviceType, dataLocale, isContactLensConsentEnabled, payLaterAllowed, handlePayLater, isContactLensCheckboxChecked,
	setIsContactLensCheckboxChecked, setSectedPrimerCreditCard, configData } : PrimeCardType) => {
	// console.log(isContactLensConsentEnabled, "isContactLensConsentEnabled");
	// console.log("payLaterAllowed", payLaterAllowed);
	// const [checked, setChecked] = useState(false);
	// const [ isPayLaterModalActive, setIsPayLaterModalActive]=useState(false);
	const toggleChecked = () => {
		setIsContactLensCheckboxChecked(!isContactLensCheckboxChecked);
	};
	// const handlePayLater = ()=>{
	// 	console.log("payLater");
	// };
	return <PrimerCardWrapper>
		<div id="my-container"></div>
		{/* Card Number Input and Error Div */}
		<div className="inputPrimer" id="checkout-card-number-input">
			<div
				id="cardNumberRequired"
				className="validateError"
			></div>
		</div>
		{/* Expiry Input and Error Div */}
		<div className="inputPrimer" id="checkout-card-expiry-input">
			<div
				id="expiryRequired"
				className="validateError"
			></div>
		</div>
		{/* CVV Input and Error Div */}
		<div className="inputPrimer" id="checkout-card-cvv-input">
			<div
				id="cvvRequired"
				className="validateError"
			></div>
		</div>
		{/* Card Name Input and Error Div */}
		<input
			id="checkout-card-holder-input"
			placeholder="Full Name"
			className="inputPrimer cardHolderInput"
		></input>
		<div
			id="cardnameRequired"
			className="validateError"
		></div>
		<button style={{ display: "none" }} className="submit-button" id="submit-button" />
		{isContactLensConsentEnabled && <ContactLensConsentCheckbox configData={configData} deviceType={deviceType} dataLocale={dataLocale} checked={isContactLensCheckboxChecked} toggleChecked={toggleChecked} />}
		<PayLaterButtonWrapper>
			{isContactLensConsentEnabled && <PayNowBtn
				className={`submit-button ${isContactLensConsentEnabled && !isContactLensCheckboxChecked ? "disabled" : ""}`}
				id="validate-button-primer"
				isContactLensConsentEnabled={isContactLensConsentEnabled}
				font={TypographyENUM.lkSansBold}
				checked={isContactLensCheckboxChecked}
				disabled={isContactLensConsentEnabled && !isContactLensCheckboxChecked}
				onClick={()=>setSectedPrimerCreditCard(true)}
			>
				{dataLocale?.PLACE_ORDER}
			</PayNowBtn>}
			{payLaterAllowed && <><PayLater dataLocale={dataLocale} handlePayLater={handlePayLater} isContactLensConsentEnabled={isContactLensConsentEnabled}
				isContactLensCheckboxChecked={isContactLensCheckboxChecked} />
				
			<PayNowBtn
				id="validate-button-primer"
				isContactLensConsentEnabled={isContactLensConsentEnabled}
				checked={isContactLensCheckboxChecked}
				font={TypographyENUM.lkSansBold}
				onClick={()=>setSectedPrimerCreditCard(true)}
			>
				<span>{dataLocale?.PAY_NOW}</span>
			</PayNowBtn>
			</>	
			}
			{!isContactLensConsentEnabled && !payLaterAllowed && <PayNowBtn
				id="validate-button-primer"
				isContactLensConsentEnabled={isContactLensConsentEnabled}
				checked={isContactLensCheckboxChecked}
				font={TypographyENUM.lkSansBold}
				onClick={()=>setSectedPrimerCreditCard(true)}
			>
				<span>{dataLocale?.PLACE_ORDER}</span>
			</PayNowBtn>}
		</PayLaterButtonWrapper>
	</PrimerCardWrapper>;
};

export default PrimerCard;
