import React from "react";
import { ProductImageTypes } from "./ProductImage.types";
import { ProductImageWrapper } from "./styles";

export default function ProductImage({ imageSrc, onClick,altText }: ProductImageTypes) {
	return (
		<ProductImageWrapper>
			<figure>
				<img
					alt={altText}
					aria-label={altText}
					src={imageSrc}
					onClick={onClick}
				/>
			</figure>
		</ProductImageWrapper>
	);
}
