import React from "react";
import { SequenceTabType } from "./SequenceTab.types";
import { SqBottomText, SqDividerLine, SqHolder, SqTextContainer, SqTopText } from "./styles";

const PackagesScreen = ({ topText, btText, currentlySelected, enabled, id, onClick, showDividerLine, isRTL= false }: SequenceTabType) => {
	return (
		<SqHolder isRTL={isRTL}>
			<SqTextContainer enabled={enabled} currentlySelected={currentlySelected} onClick={enabled ? () => onClick(id) : () => null}>
				{topText && <SqTopText>{topText}</SqTopText>}
				<SqBottomText currentlySelected={currentlySelected}>{btText}</SqBottomText>
			</SqTextContainer>
			{showDividerLine && <SqDividerLine hasTopText={Boolean(topText)}/>}
		</SqHolder>
	);
};

export default PackagesScreen;
