import React from "react";
import { TypographyENUM } from "../../../../Types/general";
import AddressInputField from "../../AddressInputField";
import AddressTextField from "../../AddressTextField";
import { FormFieldPropsType } from "./FormField.types";

export default function FormField({
	onChange = () => null,
	onBlur = () => null,
	onKeyPress,
	value,
	isError,
	error,
	isRTL,
	placeholder,
	fontSize,
	font=TypographyENUM.lkSansRegular,
	isFullWidth,
	children
}: FormFieldPropsType) {
	return (
		<AddressInputField
			isFullWidth={isFullWidth}
			font={font}
			isError={isError}
			subText={error}
		>
			<AddressTextField
				onChange={onChange}
				onKeyPress={onKeyPress}
				onBlur={onBlur}
				value={value}
				isRTL={isRTL}
				placeholder={placeholder}
				fontSize={fontSize}
			>
				{children}
			</AddressTextField>
		</AddressInputField>
	);
}
