import React from "react";
import { Radio } from "../../NewPayment";
import { Error, InputContainer, Text } from "../../NewPayment/components/RadioGroup/NewRadioGroup.styles";
import { Label, RadioGroupContainer } from "./GenderRadio.styles";
import { GenderRadioPropsType } from "./GenderRadio.types";

export default function GenderRadio({
	// row,
	// column,
	value,
	onChange,
	onBlur,
	error,
	touched,
	localeData,
	genderConfig
}: GenderRadioPropsType) {
	const handleClick = (gender: string) => {
		onChange(gender);
		onBlur(gender);
	};
	return (
		<div>
			<Label>{localeData.SELECT_GENDER as string}*</Label>
			<RadioGroupContainer isMsite={false}>
				{
					genderConfig && genderConfig.map(
						(gender:any) =>
							(<InputContainer key={gender.key}>
								<Radio
									isSelected={value === gender.key}
									onClick={() => handleClick(gender.key)}
								/>
								<Text onClick={() => handleClick(gender.key)}> {gender.label as string} </Text>
							</InputContainer>))
				}
			</RadioGroupContainer>
			{touched && <Error>{error}</Error>}
		</div>
	);
}
