import React from "react";
import { Error } from "../../NewPayment/components/RadioGroup/NewRadioGroup.styles";
import { Label, InputContainer, Text, RadioGroupContainer } from "./MsiteGenderRadio.styles";
import { GenderRadioPropsType } from "./MsiteGenderRadio.types";

export default function GenderRadio({
	// row,
	// column,
	value,
	onChange,
	onBlur,
	error,
	touched,
	localeData,
	genderConfig
}: GenderRadioPropsType) {
	const handleClick = (gender: string) => {
		onChange(gender);
		onBlur(gender);
	};

	return (
		<div>
			<Label>{localeData.SELECT_GENDER as string}*</Label>
			<RadioGroupContainer isMsite={true}>
				{ genderConfig && genderConfig.map((gender:any) =>
					(<InputContainer isSelected={value === gender.key} key={gender.key}>
						<Text onClick={() => handleClick(gender.key)}> {gender.label as string} </Text>
					</InputContainer>)
				)
				}
			</RadioGroupContainer>
			{touched && <Error>{error}</Error>}
		</div>
	);
}
