import React from "react";
import { PlusIcon, Tick } from "../../../../Icons";
import SvgNoResults from "../../../../Icons/NoResults";
import SvgWarning from "../../../../Icons/Warning";
import { DashedLine, Lens, Name, NoResultsImageContainer, NoResultsWrapper, ProductContainer, ProductDetails, ProductImage, ProductImageContainer, TryDifferentText, WarningContainer, WarningIcon, WarningMessage, WishlistButton, WishlistText } from "./MsiteNoResults.styles";
import { MsiteNoResultsPropTypes } from "./MsiteNoResults.types";

function MsiteNoResults({ items, localeData, wishlistedProductIds, saveToWishlist }: MsiteNoResultsPropTypes) {
	return (
		<NoResultsWrapper>
			<NoResultsImageContainer>
				<SvgNoResults />
			</NoResultsImageContainer>
			<WarningContainer>
				<WarningIcon>
					<SvgWarning />
				</WarningIcon>
				<WarningMessage>{ localeData.SORRY_OUT_OF_STOCK as string }</WarningMessage>
			</WarningContainer>
			<DashedLine />
			{items?.map((item) => (
				<ProductContainer key={item.id}>
					<ProductImageContainer>
						<ProductImage src={item.itemImg} />
					</ProductImageContainer>
					<ProductDetails>
						<Name>{ item.itemName }</Name>
						<Lens>{ item.itemOptions?.[0]?.label }</Lens>
						{wishlistedProductIds?.includes(item.itemId.toString()) && (
							<WishlistButton>
								<Tick />
								<WishlistText>{ localeData.WISHLISTED as string }</WishlistText>
							</WishlistButton>
						)}
						{!wishlistedProductIds?.includes(item.itemId.toString()) && (
							<WishlistButton onClick={() => saveToWishlist(parseInt(item.itemId))}>
								<PlusIcon />
								<WishlistText>{ localeData.WISHLIST_ITEM as string }</WishlistText>
							</WishlistButton>
						)}
					</ProductDetails>
				</ProductContainer>
			))}
			<TryDifferentText>{localeData.PLEASE_TRY_DIFFERENT as string }</TryDifferentText>
		</NoResultsWrapper>
	);
}

export { MsiteNoResults };