import React from "react";
import { OtherStoresWrapper } from "./OtherStores.styles";
import { OtherStoresPropsType, Store } from "./OtherStores.types";
import StoreRow from "./StoreRow";


function OtherStores({ selected, onClick, stores }: OtherStoresPropsType) {
	return (
		<OtherStoresWrapper>
			{stores.map((store: Store) => 
				<StoreRow key={store.code} store={store} selected={selected && selected.code === store.code} onClick={onClick} />
			)}
		</OtherStoresWrapper>
	);
}

export { OtherStores };