export interface OtherStoresPropsType {
  stores: Store[];
  selected: Store;
  onClick: (store: Store) => void;
}

export interface StoreRowPropsType {
  selected: boolean;
  store: Store;
  onClick: (store: Store) => void;
}

export type Store = {
  code: string;
  storeType: string;
  name: string;
  address: string;
  openingTime: string;
  closingTime: string;
  area: string;
  landmark: string;
  city: string;
  state: string;
  country: string;
  telephone: string;
  phoneCode: string;
  longitude: string;
  latitude: string;
  url: string;
  shortUrl: string;
  googleUrl: string;
  pincode: string;
  email: string;
  geoDistance?: number;
  isAvailable?: boolean;
};