import React, { ChangeEvent, memo, useCallback } from "react";
import * as Icons from "../../../../Icons/index";
import { TypographyENUM } from "../../../../Types/general";
import FormField from "../FormField/FormField";
import { IconContainer } from "./SearchBox.styles";
import { SearchBoxPropsType } from "./SearchBox.types";

function SB({ dataLocale, onSearch, value, isRTL }: SearchBoxPropsType) {
  
	const searchHandler = useCallback((e: ChangeEvent<HTMLInputElement>) => {
		onSearch(e.target.value);
	}, [onSearch]);

	return (
		<FormField
			onChange={searchHandler}
			value={value}
			isRTL={isRTL}
			placeholder={dataLocale.SEARCH_FOR_A_STORE as string}
			fontSize="var(--fs-14)"
			font={TypographyENUM.lkSansRegular}
			isFullWidth={true}
		>
			<IconContainer>
				<Icons.Search />
			</IconContainer>
		</FormField>
	);
}

const SearchBox = memo(SB);

export { SearchBox };